$("#evaluation_order .check").click(function(){var b=$(this).parent().parent().find("input");var a=$(this).parent().parent().find("textarea");if($(this).is(":checked")){b.prop("disabled",false);a.prop("disabled",false);$(a).parent().show()}else{b.prop("disabled",true);a.prop("disabled",true);$(a).parent().hide()}$(this).prop("disabled",false)});